import React, {Component} from 'react'
import { Button, Checkbox, Form, Input  } from 'semantic-ui-react';
import PropTypes from 'prop-types';
import './../css/Login.css';
import GoogleLogin from 'react-google-login';


class LoginForm extends React.Component {
	constructor(props){
		super(props);
	}
  state = {
    username: '',
    password: ''
  };

  handle_change = e => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState(prevstate => {
      const newState = { ...prevstate };
      newState[name] = value;
      return newState;
    });
  };

  render() {
  	const responseGoogle2 = (response) => {
      fetch('https://www.googleapis.com/oauth2/v3/tokeninfo?id_token='+response.tokenId, {
        method:'GET',
      })
        .then(response => response.json())
    .then(json => {
    });
    var y=response.profileObj.givenName;
    var x=response.googleId;
      let data1 = {
    "username" : "bhjkjn",
    "password": "",
    "is_staff" : false
      };
      data1['username'] = y;
      data1['password'] = x;
      fetch('http://127.0.0.1:8000/schedule/token-auth/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data1)
    })
      .then(res => res.json())
      .then(json => {
        window.localStorage.setItem('token', json.token);
        console.log(data1);
        console.log(response);
      this.setState({
        logged_in: true,
        username: response.profileObj.name,
      });
   });

  }

    return (
    	<div>
      <form onSubmit={e => this.props.handle_login(e, this.state)}>
        <h1>Log In</h1>
        <div className="Login">
        <Form.Field>
        <input
          type="text"
          placeholder="Username"
          className="input"
          name="username"
          value={this.state.username}
          onChange={this.handle_change}
        />
        </Form.Field>
        <Form.Field>
        <input
          type="password"
          placeholder="Password"
          className="input"
          name="password"
          value={this.state.password}
          onChange={this.handle_change}
        />
        </Form.Field>
         <Button color="teal" class="buttons" type='submit'>Submit</Button>
         </div>
      </form>
        <br />
        <h3> OR </h3>
        <GoogleLogin
        clientId='915718732611-k5t8orhtst2sjojldkmfkbi8khtptsob.apps.googleusercontent.com'
        buttonText="LOGIN IN  WITH GOOGLE"
        onSuccess={this.props.responseGoogle2}
        onFailure={this.props.responseGoogle2}
        />
        </div>
    );
  }
}

export default LoginForm;

LoginForm.propTypes = {
  handle_login: PropTypes.func.isRequired
};
